import React, { useState } from 'react';

function App() {
  const [menu, setMenu] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/api/menu', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ menu }),
      });

      const data = await response.json();
      setMessage(data.message);
    } catch (error) {
      setMessage('Error al guardar el menú');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center">
      <div className="bg-white p-8 rounded-lg shadow-lg max-w-md w-full">
        <h1 className="text-2xl font-bold mb-4 text-center text-gray-700">Crear Menú</h1>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700" htmlFor="menu">
              Menú:
            </label>
            <input
              type="text"
              id="menu"
              value={menu}
              onChange={(e) => setMenu(e.target.value)}
              className="mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
              placeholder="Escribe el menú..."
            />
          </div>
          <button
            type="submit"
            className="w-full bg-indigo-500 text-white p-2 rounded-md hover:bg-indigo-600 transition-colors duration-300"
          >
            Guardar Menú
          </button>
        </form>
        {message && (
          <div className="mt-4 p-4 bg-blue-100 text-blue-700 rounded-lg">
            {message}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
